import React, { useState, useCallback } from "react";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import ExpenseSummary from "./components/ExpenseSummary";
import { Button } from "./components/ui/button";

export default function App() {
  const [expenses, setExpenses] = useState([]);
  const [editingExpense, setEditingExpense] = useState(null);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const [showExpenseList, setShowExpenseList] = useState(false);

  const addExpense = (expense) => {
    const newExpense = { ...expense, id: Date.now().toString() };
    setExpenses([...expenses, newExpense]);
  };

  const deleteExpense = (id) => {
    setExpenses(expenses.filter((expense) => expense.id !== id));
  };

  const editExpense = (id, updatedExpense) => {
    setExpenses(
      expenses.map((expense) =>
        expense.id === id ? { ...updatedExpense, id } : expense
      )
    );
    setEditingExpense(null);
  };

  const filterExpenses = useCallback(() => {
    const filtered = expenses.filter((expense) => {
      const expenseDate = new Date(expense.date);
      const start = startDate ? new Date(startDate) : new Date(0);
      const end = endDate ? new Date(endDate) : new Date();
      return expenseDate >= start && expenseDate <= end;
    });
    setFilteredExpenses(filtered);
  }, [expenses, startDate, endDate]);

  const toggleExpenseList = () => {
    setShowExpenseList(!showExpenseList);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4">
        <h1 className="text-3xl font-bold text-center mb-8">Expense Tracker</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div>
            <ExpenseForm
              onAddExpense={addExpense}
              editingExpense={editingExpense}
              onEditExpense={editExpense}
            />
            <ExpenseSummary
              expenses={expenses}
              filteredExpenses={filteredExpenses}
              startDate={startDate}
              endDate={endDate}
              onStartDateChange={setStartDate}
              onEndDateChange={setEndDate}
              onFilter={filterExpenses}
            />
            <div className="mt-6">
              <Button onClick={toggleExpenseList} className="w-full">
                {showExpenseList ? "Hide Expenses" : "View Expenses"}
              </Button>
            </div>
          </div>
          {showExpenseList && (
            <ExpenseList
              expenses={expenses}
              onDeleteExpense={deleteExpense}
              onEditExpense={setEditingExpense}
              onClose={toggleExpenseList}
            />
          )}
        </div>
      </div>
    </div>
  );
}
